namespace Carbon.Helpers.Tests
{
	using System;

	using NUnit.Framework;

	[TestFixture]
	public class HexStringTests
	{
		private const string base64EncodedClearGif = "";

		[Test]
		public void Test()
		{
			var bytes = Convert.FromBase64String(base64EncodedClearGif);

			var hexString = HexString.FromBytes(bytes);

			Assert.AreEqual(
				/*expected*/ "",
				/*actual*/ hexString
			);

			Assert.AreEqual(bytes, HexString.ToBytes(hexString));
		}
	}
}
